public class Main {

    public static void main(String[] args) {
        String dlouheCislo = "";

        long nejvetsiSoucin = najdiNejvetsiSoucin(dlouheCislo, 13);
        System.out.println(nejvetsiSoucin);
    }

    private static long najdiNejvetsiSoucin(String cislo, int delkaSouboru) {
        long nejvetsiSoucin = 0;

        for (int i = 0; i <= cislo.length() - delkaSouboru; i++) {
            String podretezec = cislo.substring(i, i + delkaSouboru);
            long soucin = vypocitejSoucin(podretezec);

            if (soucin > nejvetsiSoucin) {
                nejvetsiSoucin = soucin;
            }
        }

        return nejvetsiSoucin;
    }

    private static long vypocitejSoucin(String cislo) {
        long soucin = 1;

        for (int i = 0; i < cislo.length(); i++) {
            int cislice = Character.getNumericValue(cislo.charAt(i));
            soucin *= cislice;
        }

        return soucin;
    }
}
